shouldComponentUpdate(nextProps, nextState) {
  var output = [];
  for(var key in nextProps) {
    if (nextProps[key] !== this.props[key]) {
      output.push([`[props.${key}]`, this.props[key], '!=', nextProps[key]]);
    }
  }
  if (this.state) {
    for(var key in nextState) {
      if (nextState[key] !== this.state[key]) {
        output.push([`[state.${key}]`, this.state[key], '!=', nextState[key]]);
      }
    }
  }

  if (output.length) {
    console.log(`${this.constructor.name}.shouldComponentUpdate`);
    output.forEach(o => console.log.apply(console, ['\t'].concat(o)));
  }
  return true
}
