shouldComponentUpdate(nextProps, nextState) {
    var output = [];
    var collapsed = true;
    const format = (v) => typeof v === 'function' ? 'function' : v;
    for(var key in nextProps) {
      if (nextProps[key] !== this.props[key]) {
        output.push({
          label: `props.${key}`,
          props: format(this.props[key]),
          nextProps: format(nextProps[key]),
        });
      }
    }
    if (this.state) {
      for(var key in nextState) {
        if (nextState[key] !== this.state[key]) {
          output.push({
            label: `state.${key}`,
            props: format(this.state[key]),
            nextProps: format(nextState[key]),
          });
        }
      }
    }

    if (output.length) {
      var name = `${this.constructor.name}.shouldComponentUpdate`;
      if (collapsed) {
        console.groupCollapsed(`${name} [${output.map(o => o.label).join(', ')}]`)
      } else {
        console.group(name);
      }
      output.forEach(o => {
        console.group(o.label);
        console.log(o.props);
        console.log(o.nextProps);
        console.groupEnd();
      })
      console.groupEnd();
    }
    return true
  }
