shouldComponentUpdate(nextProps, nextState) {
  for(var key in nextProps) { if (nextProps[key] !== this.props[key]) { console.log(this.constructor.name, 'props', key, this.props[key], nextProps[key]); } }
  if (this.state) {
    for(var key in nextState) { if (nextState[key] !== this.state[key]) { console.log(this.constructor.name, 'state', key, this.state[key], nextState[key]); } }
  }
  return true
}
